import type { SeedModule, SeedLesson, SeedQuestion } from "./seed-data-1";

function q(order: number, type: "MCQ" | "SCENARIO", qEn: string, qEs: string, opts: [string,string,string,string,string,string,string,string], correct: string, exEn: string, exEs: string, scEn?: string, scEs?: string): SeedQuestion {
  return {
    type, order, questionEn: qEn, questionEs: qEs, correctOptionId: correct,
    explanationEn: exEn, explanationEs: exEs,
    ...(scEn ? { scenarioEn: scEn, scenarioEs: scEs } : {}),
    options: [
      { id: "a", textEn: opts[0], textEs: opts[1] },
      { id: "b", textEn: opts[2], textEs: opts[3] },
      { id: "c", textEn: opts[4], textEs: opts[5] },
      { id: "d", textEn: opts[6], textEs: opts[7] },
    ],
  };
}
function lesson(order: number, tEn: string, tEs: string, cEn: string, cEs: string): SeedLesson {
  return { order, titleEn: tEn, titleEs: tEs, contentEn: cEn, contentEs: cEs };
}

